import React, {PureComponent} from 'react'
import './filter.scss';

export default class Filter extends PureComponent {

  buttons = [
    { name : "new", title : "Новинки" },
    { name : "action", title : "Акции" },
    { name : "popular", title : "Популярные" },
    { name : "price", title : "По цене" },
  ]




  state = {
    new     : false,
    action  : false,
    popular : false,
    price   : false
  }

  handleChange = (e) => {
    console.log( e );
  };


  render(){
    const buttons = this.buttons.map (({ name, title }) => {
      return (
        <label key={name}>
          {title}
          <input
            name={name}
            type="checkbox"
            onChange={this.handleChange}
          />
        </label>
      )
    })

    return (
      <div className="fiter">
        { buttons }
      </div>
    )
  }
}
