import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  constructor() {
    super();
    this.state = {
      filters: []
    };
    this.food = [
      {
        type: "fruits",
        name: "Banana",
        image:
          "http://www.arrms.org/media/blogs/blog/banana.jpg?mtime=1417645870"
      },
      {
        type: "meat",
        name: "Steak",
        image: "url"
      },
      {
        type: "drinks",
        name: "Juice",
        image: "url"
      }
    ];
  }

  filter2 = event => {
    const checkboxes = event.currentTarget.getElementsByTagName("input");
    console.log(checkboxes);
    const filters = [];
    Object.values(checkboxes).map(checkbox => {
      if (checkbox.checked) {
        filters.push(checkbox.value);
      }
    });
    this.setState({ filters });
  };

  render() {
    return (
      <div>
        <form onChange={this.filter2}>
          <label htmlFor="fruits">Акции</label>
          <input
            className="fruits"
            value="action"
            type="checkbox"
            name="fruits"
          />
          <label htmlFor="meat">Новые</label>
          <input value="new" type="checkbox" name="meat" />

        </form>
        <ul>
          {json
            .filter(item => this.state.filters.indexOf(item.new) >= 0)
            .map((filtered, index) => {
              return (
                <li key={index}>
                  {filtered.name}
                  <img src={filtered.image} />
                </li>
              );
            })}
        </ul>
      </div>
    );
  }
}

