import React, {PureComponent} from 'react'
import './filter.scss';

export default class Filter extends PureComponent {

  buttons = [
    { name : "new", title : "Новинки" },
    { name : "action", title : "Акции" },
    { name : "popular", title : "Популярные" },
    { name : "price", title : "По цене" },
  ]

  state = {
    new: false,
    action: false,
    popular: false,
    price: false
  };

  filterItems = (name, title, event, props) => {
    console.log([name]);
    this.setState({
      [name]: event.target.checked
    });
  };



  render(){
    const buttons = this.buttons.map (({ name, title }) => {
      return (
        <label key={name}>
          {title}
          <input
            name={name}
            type="checkbox"
            onChange={ (event) => this.filterItems(name, title, event) }
          />
        </label>
      )
    })

    return (
      <div className="fiter">
        { buttons }
      </div>
    )
  }
}
