import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  state = {
    new : false,
    action : false
  };


  filterItems = ({ target: { name, checked } }) => {
    this.setState({
      [name]: checked,
    });
  }


  render() {

    var msg;
    if (this.state.checked) {
      msg = "checked";
    } else {
      msg = "unchecked";
    }

    return (
      <div>
        <label> Новинки:<input type="checkbox" onChange={ this.props.filterItems('new') } /> </label>
        <label> Акции:<input type="checkbox" onChange={ this.props.filterItems('action') } /> </label>
        <p>this box is {msg}</p>
      </div>
    )
  }
}
