import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
import Filtered from '../filtered/filtered.js'
import Search from '../search/search.js';

export default class App extends PureComponent {
  // задаём стейт
  state = {
    search: undefined,
    filters: [],
    items: json,
  };

  // проверяем какие фильтры выбрал юзер
  onFilterChange = ( event ) => {
    const checkboxes = [...event.currentTarget.closest(".filter").getElementsByTagName("input")]
    const filters = [];
    checkboxes.map(checkbox => {
      if (checkbox.checked) {
        filters.push(checkbox.name);
      }
    });
    this.setState({ filters }, this.filtredInput);
  }

  // фильтруем согласно выбранным фильтрам
  filtredInput() {
    // к каждому element примеряем filters.every, где в filters будут лежать фильтры

    if(this.state.filters.length > 0) {
      const items = this.state.items.filter(element => this.state.filters.every(key => element[key]));
      this.setState( {items} )
    } else {
      this.setState( {items : json} )
    }
  }

  // смотрим, что вводит юзер
  onSearchChange = ( {currentTarget} ) => {
    const search = currentTarget.value
    // let items

    // if(this.state.items.length) {
    //   items = this.state.items
    // } else {
    //   items = json
    // }

    this.setState({ search }, this.filtredSearch )
  }

  // фильтруем соглассно вводу юзера
  filtredSearch() {
    const items = this.state.items.filter(word =>
        word.name.toLocaleLowerCase().indexOf(this.state.search.toLocaleLowerCase()) !== -1
    )
    this.setState( {items} )
  }

  render() {
    return (
      <div>
        <div className="navigation">
          <Filter onFilterChange={this.onFilterChange} />
          <Search onSearchChange={this.onSearchChange} />
        </div>
        {
          this.state.items.length
            ? <Filtered items={this.state.items} /> // если фильтры есть
            : <Filtered items={this.state.items} /> // если фильтров нет
        }
      </div>
    )
  }
}
