import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
import Filtered from '../filtered/filtered.js'
import Search from '../search/search.js';

export default class App extends PureComponent {
  // задаём стейт
  state = {
    search: "",
    filters: [],
    items: [],
  };
  // проверяем какие фильтры выбрал юзер, а потом добавляем это в стейт
  filterItems = ( event ) => {
    const checkboxes = [...event.currentTarget.closest(".filter").getElementsByTagName("input")]
    const filters = [];
    checkboxes.map(checkbox => {
      if (checkbox.checked) {
        filters.push(checkbox.name);
      }
    });
    this.setState({ filters });

  }

  // поиск по айтемам
  searchItems = ( {currentTarget} ) => {
    const search = currentTarget.value
    let items

    this.setState({ search })

    // console.log( this.state.items );

    if(this.state.items.length) {
      filter(this.state.items)
    } else {
      filter(json)
    }

  }


  filter = (nonFiltred) => {
    const result = nonFiltred.filter(word =>
      word.name
        .toLocaleLowerCase()
        .indexOf(this.state.search.toLocaleLowerCase()) !== -1
    )

    // К каждому element примеряем filters.every, где в filters будут лежать фильтры
    const items = result.filter(element => filters.every(key => element[key]));
    this.setState( {items} )
  }

  render() {
    return (
      <div>
        <div className="navigation">
          <Filter filterItems={this.filterItems} />
          <Search searchItems={this.searchItems} />
        </div>
        {
          this.state.filters.length
            ? <Filtered items={this.state.items} /> // если фильтры есть
            : <Filtered items={false} /> // если фильтров нет
        }
      </div>
    )
  }
}
