import React, {PureComponent} from 'react'
import './filter.scss';

export default class Filter extends PureComponent {

  buttons = [
    { name : "new", title : "Новинки" },
    { name : "action", title : "Акции" },
    { name : "popular", title : "Популярные" },
    { name : "price", title : "По цене" },
  ]

  state = {isToggleOn: false};

  deleteRow = (e, name, title) => {
    // this.setState(state => ({
    //   isToggleOn: true ? true : false
    // }));
    console.log(e);
  };



  render(){
    const buttons = this.buttons.map (({ name, title }) => {
      return (
        <label key={name}>
          {title}
          <input
            name={name}
            type="checkbox"
            onChange={ (e) => this.deleteRow(name, title) }
          />
        </label>
      )
    })

    return (
      <div className="fiter">
        { buttons }
      </div>
    )
  }
}
