import React, {PureComponent} from 'react'
import './filter.scss';

export default class Filter extends PureComponent {

  state = {
    new     : false,
    action  : false,
    popular : false,
    price   : false
  }

  handleChange = (event, name) => {
    console.log( name );
  };


  render(){
    return (
      <div className="fiter">
        <label><div>Новинки <input name="new" onChange={this.handleChange('new')} type="checkbox"/></div></label>
        <label><div>Акции <input name="action" onChange={this.handleChange('action')} type="checkbox"/></div></label>
        <label><div>Популярные <input name="popular" onChange={this.handleChange('popular')} type="checkbox"/></div></label>
        <label><div>По цене <input name="price" onChange={this.handleChange('price')} type="checkbox"/></div></label>
      </div>
    )
  }
}
