import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  state = {
    new: false,
    action: false,
    popular: false,
    price: false
  };

  filterItems = ({ target: { name, checked } }) => {
    this.setState({
      [name]: checked,
    });

    const item = json.filter(element => this.state.action && this.state.new)

    console.log(item);
  }


  render() {
    let txt
    if (this.state.new == true) {
      txt = "checked"
    } else {
      txt = "uchecked"
    }

    let txt1
    if (this.state.action == true) {
      txt1 = "checked"
    } else {
      txt1 = "uchecked"
    }

    return (
      <div>
        <Filter filterItems={this.filterItems} />
        <Item />
        <p>
          Новинки {txt}
        </p>
        <p>
          Акции {txt1}
        </p>
      </div>
    )
  }
}

