import React, {PureComponent} from 'react'
import json from '../json/json.js'

export default class Filtered extends PureComponent {
  // рендерим все айтемы и пакуем в переменную
  render() {
    let result
    if(this.props.items) {

    result = this.props.items.map((item, i) => {
      return (
        <div key={i} className="item">
          <div><img src={item.img} width="250px" height="250px"></img></div>
          <h2>{item.name}</h2>
          <p>{item.components}</p>
          <p><span>{item.mass}</span></p>
          <p><span>{item.price}</span></p>
          <button>Заказать</button>
        </div>
      )
    })

  } else {
    result = json.map((item, i) => {
      return (
        <div key={i} className="item">
          <div><img src={item.img} width="250px" height="250px"></img></div>
          <h2>{item.name}</h2>
          <p>{item.components}</p>
          <p><span>{item.mass}</span></p>
          <p><span>{item.price}</span></p>
          <button>Заказать</button>
        </div>
      )
    })
  }

    // вставляем айтемы в компонент
    return (
      <div className="content">{result}</div>
    )
  }
}
