import React, {PureComponent} from 'react'
import './App.scss';
import Shop from '../../page/shop/shop.js'

import {
  Route,
  Switch,
  Redirect,
  withRouter
} from "react-router"

export default class App extends PureComponent {
  render() {

    return (
      <div className="App">
        <Router>
        <Switch>
          <Shop history={history} path='/home' component={Shop} />
          <Shop history={history} path='/shop' component={Shop} />
          <Redirect from='/' to='/home'/>
        </Switch>
        </Router>
      </div>
    );
  }
}
