import React, {PureComponent} from 'react'
import './filter.scss';

export default class Filter extends PureComponent {

  state = {
    new : false
  }

  handleChange = (event) => {
    // console.log(event);

    this.filter = event.target.name


    // this.setState({
    //   new : !true
    // })

    console.log( this.filter );
  };


  render(){
    return (
      <div className="fiter">
        <label><div>Новинки <input name="new" onChange={this.handleChange} type="checkbox"/></div></label>
        <label><div>Акции <input name="action" type="checkbox"/></div></label>
        <label><div>Популярные <input name="popular" type="checkbox"/></div></label>
        <label><div>По цене <input name="price" type="checkbox"/></div></label>
      </div>
    )
  }
}
