import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  state = {
    filter : {
      new: false,
      action: false,
      popular: false,
      price: false
    }
  };


  filterItems = ({ target: { name, checked } }) => {
    this.setState({
      filter: { [name] : checked, }
    });


    // console.log( this.state[name] );
    const filters = Object.entries( this.state.filter ).filter(n => n[1]).map(n => n[0]);
    const filteredItems = this.state.filter.filter(n => filters.every(m => n[m]));

    console.log(filters[0]);
    console.log('---');
    // console.log(filteredItems);
  }


  render() {
    let txt
    if (this.state.filter.new == true) {
      txt = "checked"
    } else {
      txt = "uchecked"
    }

    let txt1
    if (this.state.filter.action == true) {
      txt1 = "checked"
    } else {
      txt1 = "uchecked"
    }

    return (
      <div>
        <Filter filterItems={this.filterItems} />
        <Item />
        <p>
          Новинки {txt}
        </p>
        <p>
          Акции {txt1}
        </p>
      </div>
    )
  }
}

