import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  state = {
    checked : false
  };

  constructor() {
    super()
    this.msg ='sdsd'
}

  filterItems = () => {
    this.setState({
      checked: !this.state.checked
    });

    if (this.state.checked) {
      msg = "checked";
    } else {
      msg = "unchecked";
    }

  };


  render() {

    const text = this.msg ? this.msg : "не выбранно"

    return (
      <div>
        <label> Новинки:<input type="checkbox" onChange={this.filterItems} /> </label>
        <p>Cтейт новинок: {text} </p>
      </div>
    )
  }
}
