import React, {PureComponent} from 'react'
import './App.scss';
//
import json from '../json/json.js'
import Filter from '../filter/filter.js'
//
import Item from '../item/item.js'
// import { Route } from "react-router";

export default class App extends PureComponent {
  state = {
    new : false,
    action : false
  };


  filterItems = (name) => {
    this.setState(state => {
      const newState = { ...state }
      newState = !this.state.checked
      return newState
    })

  };


  render() {

    var msg;
    if (this.state.checked) {
      msg = "checked";
    } else {
      msg = "unchecked";
    }

    return (
      <div>
        <label> Новинки:<input type="checkbox" onChange={ () => {this.filterItems('new')} } /> </label>
        <label> Акции:<input type="checkbox" onChange={ () => {this.filterItems('action')} } /> </label>
        <p>this box is {msg}</p>
      </div>
    )
  }
}
