import React, {PureComponent} from 'react'
import './App.scss';
import Shop from '../../page/shop/shop.js'
import AboutUs from '../../page/aboutUs/aboutUs.js'
import Menu from '../menu/menu.js'
import { BrowserRouter, Switch, Route, Redirect, Router } from "react-router-dom";

export default class App extends PureComponent {
  state = {
    shoppingBasketItems: 0
  }

  updateShoppingBasket(event) {
    const name = event.target.closest(".item").querySelector(".item__name").textContent
    let quantity = 0
    let shoppingBasketItems = []
    shoppingBasketItems.push(name, quantity++)
    console.log( shoppingBasketItems );
  }


  render() {

    return (

      <main>
        <BrowserRouter>
          <Menu shoppingBasketItems={this.state.shoppingBasketItems} />
          <Switch>
            <Route path="/" exact children={<Shop />} />
            <Route path="/aboutus" children={<AboutUs updateShoppingBasket={this.props.updateShoppingBasket} />} />
            <Redirect to="/" />
          </Switch>
        </BrowserRouter>
      </main>

    );
  }
}
